import React from "react";
import DataInput from "./Input";

function SolutionKnapsack(){

    let count= 1;

    function displayExtraInput(){
            count+=1 ;
            console.log(count);
            return (
                <DataInput />
            )
    }
    function removeInput(){
        if(count>0){
            count-=1 ;
        console.log(count);
        } 
}



    return(
        <div className= "container" style={{marginTop: "50px"}}>
        <div className="row">
            <div className="col-lg-6">

            {/* To get the max weight of the knapsack */}
                <div className="max-weight">
                    <p>Capacity of the knapsack</p>
                    <input type="number"></input>
                </div>

            {/* To get respective items data */}
            <div id= "item-data" style={{marginTop: "20px"}}>
                <button className= "btn btn-primary" onClick={displayExtraInput}>Add item</button>
                <button className= "btn btn-primary" onClick={removeInput} style={{marginLeft: "10px"}}>Remove item</button>
                
                <DataInput />
            </div>
            </div>
        </div>
            
        </div>
    )
}

export default SolutionKnapsack;
