import React from "react";
import jsgraphs from "js-graph-algorithms";

function DijkstraSolution(props){

    let numberOfNodes= props.nodeCount;


    var g = new jsgraphs.WeightedDiGraph(numberOfNodes);

    (props.mainData).forEach(NodeRelation => {
        
        let start= parseInt(NodeRelation.starNode);
        let end= parseInt(NodeRelation.endNode);
        let cost= NodeRelation.cost;

        // Creation of Edge
        g.addEdge(new jsgraphs.Edge(start, end, cost));
    });

    // Start Node for algorithm
    var dijkstra = new jsgraphs.Dijkstra(g, 0);

    // End Node
    var v=2;

    if(dijkstra.hasPathTo(v)){
    
        var path = dijkstra.pathTo(v);
                
        for(var i = 0; i < path.length; ++i) {
            var e = path[i];
            console.log(e.from() + ' => ' + e.to() + ': ' + e.weight);
        }
        
        console.log('distance: '  + dijkstra.distanceTo(v) );
}






    return(
        <div>
            <h1>Solution</h1>

        </div>
    )
}

export default DijkstraSolution;
