import React, {useState} from "react";

function Solution(props){

    const dataArray =[];
    const maxWeight= props.maxWeight;
    for(let i=0;i<props.mainData.length;i++){
        let ratio= props.mainData[i].value1/props.mainData[i].value2;
        
        dataArray.push(ratio);
    }
    dataArray.sort();

    let ans= [];


    function getAns(){
        let w=0;

        for(let i=dataArray.length-1;i>=0;i--){

            if(w+dataArray[i]<maxWeight){
                ans.unshift(1);
                w+= props.mainData[i].value2;
            } else{
                let x= (maxWeight-w)/ props.mainData[i].value2;
                ans.unshift(x);
                w= maxWeight;
            }
        }

        console.log(ans);

    }






    return (
        <div>
            <h1>Solution : </h1>
            <br></br>
            <h1>Weight of Knapsack : {props.maxWeight}</h1>
            {getAns()}

        </div>
        

    )
}

export default Solution;
