import React, {useState} from "react";
import Input from "./input";
import DijkstraSolution from "./solution";


function Solution(){

    const[inputs, setInputs]= useState(0);
    const[value, setValue]= useState(false);
    const [solution , setSolution]= useState(false);

    function handleChangeNumberOfInputs(event){
        let value= event.target.value;
        setInputs(value);
    }

    const [cellValue, setCellValue]= useState(0);

    function handleChange(event){
        let x= event.target.value;
        setCellValue(x);
    }

    let x=[]
    

    function getCells(){

        let count=0;

        for(let i=0;i<inputs;i++){
            for(let j=0;j<inputs;j++){

                x.push( <input key={count} type="number" value={cell} onChange={(e) => handleChange(e)} className= "cell"></input>)
                count++;
            }
            x.push(<br></br>)
            
        }
            
        console.log(x);
            return(
                <p>{x}</p>
            )
        
    }

    function getSolution(){
        setSolution(true);
    }


    return(
        <div>
            <div className="container-fluid form" style={{marginTop: "20px"}}>
            
                <div className= "row">

                    <div className= "col-lg-6 col-sm-12">
                        <input
                            type="number"
                            placeholder= "Number of Nodes"
                            onChange= {e =>{handleChangeNumberOfInputs(e)}}
                        >
                        </input>

                        <button className= "btn btn-primary getInputs" onClick= {() =>{ setValue(true)} }>Insert data</button>
                        <br></br>
                        <h3>Adjacency Matrix: write the edge weight in each cell which has an edge</h3>
                        
                        {value ?  

                        getCells()
                        
                        : null}

                        <button className= "btn btn-primary getSolution" onClick= {() => getSolution()}>Get Solution</button>
                        
                    </div>

                    <div className="col-lg-6">
                        {solution ? <DijkstraSolution data= {x}/>: null}
                    </div>
                        
                </div>
            </div>
        </div>        
        

    )
}


export default Solution;
