import React, {useState} from "react";
import Input from "./input";


function Solution(){

    const[inputs, setInputs]= useState(0);
    const[value, setValue]= useState(false);

    function handleChangeNumberOfInputs(event){
        let value= event.target.value;
        setInputs(value);
    }

    let x=[]
    

    function getCells(){

       
        for(let i=0;i<inputs;i++){
            for(let j=0;j<inputs;j++){

                x.push(<Input key={Math.random()}/>)

            }
            x.push(<br></br>)
        }
            
        console.log(x);
            return(
                <p>{x}</p>
            )
        
    }


    return(
        <div>
            <div className="container-fluid form" style={{marginTop: "20px"}}>
            
                <div className= "row">

                    <div className= "col-lg-8">
                        <input
                            type="number"
                            placeholder= "Number of Nodes"
                            onChange= {e =>{handleChangeNumberOfInputs(e)}}
                        >
                        </input>

                        <button className= "btn btn-primary getInputs" onClick= {() =>{ setValue(true)} }>Insert data</button>
                        <br></br>
                        
                        {value ?  

                        getCells()
                        
                        : null}

                        <button className= "btn btn-primary getSolution">Get Solution</button>
                        
                    </div>
                        
                </div>
            </div>
        </div>        
        

    )
}


export default Solution;
