    //                  <input type="number"></input>
    //             </div>

    //                 {/* To get respective items data */}
    //             <div id= "item-data" style={{marginTop: "20px"}}>
    //                 <button className= "btn btn-primary" onClick={() => handleAdd()}>Add item</button>
    //                 <button className= "btn btn-primary" onClick={() => handleremove()} style={{marginLeft: "10px"}}>Remove item</button>

    //                 <DataInput />
    //             </div>

    //         </form>
           
    //         </div>
    //     </div>
            
    //     </div>
    // )
}

export default SolutionKnapsack;
