import React, {useState} from "react";
import DataInput from "./Input";

function SolutionKnapsack(){

    const [fields, setFields] = useState([{ value1: null, value2: null }]);
  
    function handleChangeValue1(i, event) {
      const values = [...fields];
      values[i].value1 = event.target.value;
      setFields(values);
    }
  
    function handleAdd() {
      const values = [...fields];
      values.push({ value1: null, value2: null });
      setFields(values);
    }
  
    function handleRemove(i) {
      const values = [...fields];
      values.splice(i, 1);
      setFields(values);
    }
  
    return (
      <div className="container" style={{marginTop: "20px"}}>
      <div className= "row">
          <div className= "col-lg-6">

                <button type="button" className="btn btn-primary" onClick={() => handleAdd()}>
                Add Item
                </button>
        
                {fields.map((field, idx) => {
                return (
                    <div key={`${field}-${idx}`}>
                    <input
                        type="number" 
                        value={field.value1} 
                        placeholder="Enter Profit" 
                        onChange={e => handleChangeValue1(idx, e)}
                    />
                    <input
                        type="number" 
                        value={field.value1} 
                        placeholder="Enter Weight" 
                        onChange={e => handleChangeValue1(idx, e)}
                    />
                    <button type="button" className= "btn btn-primary" onClick={() => handleRemove(idx)}>
                        Remove item
                    </button>
                    </div>
                );
                })}

          </div>
      </div>
  
        
      </div>
    );

    

