import React from "react";
import {Link} from "react-router-dom";

function Card(props){

    var customStyle= {
        width : "35rem",
        height: "25rem",
        margin : "0 auto",
        backgroundColor: "#f6376d", 
        borderRadius: "10px"
    }

    if(props.name==="Graphs"){
        customStyle.backgroundColor= "#263145"
    }
    return(
        <div id="mycard">
        <div class="col-lg-4 col-sm-6">
        <div class="card" style={customStyle}>

        
                <div class="card-body">
                <h5 class="card-title">{props.name}</h5>
                <p class="card-text" >{props.desc}</p>

                {/* This is for routing to the specific pages on click */}
                <Link to= {props.link}>
                <button class="btn btn-primary ">Solve Algorithm</button>
                </Link>
                
                
                </div>
            </div>
        </div>
            
        </div>
    )
}

export default Card;
